<script>
// Import Swiper Vue.js components
import { Swiper, SwiperSlide } from 'swiper/vue';
import Skeleton from '@/components/tools/Skeleton.vue';
import { Autoplay, Pagination } from "swiper";
import Ambassador from '@/helpers/ambassador';
// Import Swiper styles
import 'swiper/css';
import "swiper/css/pagination";

export default {
    components: {
        Swiper,
        SwiperSlide,
        Skeleton,
    },
    data() {
        return {
            slides: {},
            error: false,
            loading: true,
        }
    },
    setup() {
        return {
            modules: [Autoplay, Pagination],
        };
    },
    mounted() {
        Ambassador.getBanners(5)
            .then((response) => {
                this.slides = response.data.data.banners;
            })
            .catch((error) => {
                console.log("sorry there was an error " + error);
                this.error = true;
            })
            .finally(() => { this.loading = false });
    }
};
</script>

<template>
    <div>
        <div class="top-banner">
            <Skeleton :isHidden="!loading">
                <swiper :spaceBetween="30" :pagination="true" :modules="modules" :loop="true"
                    :autoplay="{ delay: 2500, disableOnInteraction: false, }"
                    class="swiper-container p-relative swiper-container-horizontal">
                    <swiper-slide v-for="(item, index) in slides" :key="index"
                        class="banner-item p-relative ts-dot-4 border-box over-hidden">
                        <div class="banner-container bg-no-repeat bg-cover bg-center p-relative">
                            <a :href='item.jump_url'>
                                <div class="banner-img w-100 h-100 p-absolute p-zero bg-cover bg-center"
                                    :style="'background-image: url(' + item.image_url + ');'"></div>
                            </a>
                        </div>
                    </swiper-slide>
                </swiper>
            </Skeleton>
        </div>
    </div>
</template>

<style scoped>
.top-banner {
    padding: 1.33333rem 0;
    filter: drop-shadow(0 8px 8px rgba(0, 0, 0, .12));
}

.top-banner .swiper-container {
    z-index: 0;
}

.top-banner .swiper-wrapper {
    transition-timing-function: cubic-bezier(.22, .58, .12, .98) !important;
}

.top-banner .swiper-wrapper .banner-item .banner-container {
    padding-top: 50%;
    background-color: #0b0b0b;
    background-image: url(data:image/png;base64,);
    background-size: 30%;
    border-radius: .66667rem;
}

.top-banner .swiper-wrapper .banner-item .banner-img {
    border-radius: .66667rem;
}
</style>